var expect = require('chai').expect;
var Tense = require('..');

describe('tense', function(){
  describe('unattached', function(){
    var tense = new Tense();
    it('should pluralize', function(){
      var pluralRadius = tense.pluralize('radius');
      expect(pluralRadius).to.equal('radii');
    });
    it('should singularize', function(){
      var singularBeer = tense.singularize('beers');
      expect(singularBeer).to.equal('beer');
    });
  });
  describe('attached', function(){
    var tense = new Tense();
    tense.attach();
    it('should pluralize', function(){
      var pluralRadius = 'radius'.pluralizeNoun();
      expect(pluralRadius).to.equal('radii');
    });
    it('should singuluralize', function(){
      var singularBeer = 'beers'.singularizeNoun();
      expect(singularBeer).to.equal('beer');
    });
  });
});
